import React, { useState } from 'react';
import './Header.css'; // Make sure to create a corresponding CSS file

const Header = () => {
  const [searchInput, setSearchInput] = useState('');

  const handleSearchChange = (event) => {
    setSearchInput(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    // Implement the search functionality, maybe call an API or update state
    console.log('Searching for:', searchInput);
  };

  return (
    <header className="Header">
      <div className="Header-logo">
        {/* You can place an img tag here with your logo */}
        <img src="https://assets-global.website-files.com/6303d0eec45c7627e2142f53/63cb0a475bb3ee42023fd4d1_push-refresh-logo-square.svg" alt="Logo" />
      </div>
      <form className="Header-search" onSubmit={handleSearchSubmit}>
        <input
          type="text"
          placeholder="Search..."
          value={searchInput}
          onChange={handleSearchChange}
        />
        <button type="submit">Search</button>
      </form>
      <div className="Header-support">
        <button onClick={() => console.log('Support')}>
          Support
        </button>
      </div>
    </header>
  );
};

export default Header;
